mapper.select:
SELECT FROM ( SELECT p.* FROM tbl_gz_posts AS p JOIN tbl_gz_users AS u ON p.user_id = u.id AND u.school_id = #{schoolId} AND p.meet_datetime >= UTC_TIMESTAMP() AND p.title LIKE ('%' #{searchKeyword} '%') ) AS p WHERE ( SELECT COUNT(*) FROM tbl_gz_course_infos as i JOIN tbl_gz_courses as c ON i.course_id = c.id JOIN tbl_gz_users as cu ON cu.id = c.user_id AND cu.id = #{userId} WHERE DATE_FORMAT(p.meet_datetime,'%h%i') BETWEEN DATE_FORMAT(i.start_time, '%h%i') AND DATE_FORMAT(i.end_time, '%h%i') ) = 0
AND ( SELECT COUNT(*) FROM tbl_gz_hashtags as h WHERE h.post_id = p.id AND h.title IN (
#{hashTag}
) ) > 0
ORDER BY created_at DESC LIMIT #{limit}
